INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Hello World!', 'Написать в консоли Hello, World!', 1, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Три слова', 'Заданы три слова, вывести их в обратном порядке.', 2, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Пирамида', 'Заданы 6 символов. Напечатать их в виде пирамиды.', 3, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('ASCII код', 'Задан символ. Напечатать его ASCII код.', 4, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Среднее геометрическое', 'Заданы 3 числа. Вывести их среднее геометрическое.', 5, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Целая часть среднего геометрического', 'Заданы 4 вещественных числа. Напечатать целую часть их среднего геометрического.', 6, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Периметри и площадь', 'Заданы длины сторон треугольника AB, BC, AC. Напечатать периметр и площадь треугольника, а также углы A, B, C.', 7, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Поменять переменные', 'Заданы 2 числа. Поменять значение переменных между собой и вывести их на экран.', 8, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Пятая цифра', 'Задано шестизначное число. Вывести его 5ую цифру.', 9, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Поменять больше переменных', 'Заданы 3 числа. Поменять значения переменных. Первая переменная должна хранить значение второй, вторая должна хранить значение третьей, третья должна хранить значение первой.', 10, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Операции с числами', 'Заданы 2 числа. Вывести результат: сложения, вычитания, умножения, деления первого на второе нацело, деление первое на второе без остатка, логического сложения, логического умножения, логического исключающего или, смещения влево первого числа на второе, смещение вправо первого числа на второе.', 11, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Арифметическая прогрессия', 'Заданы первый член арифметической прогрессии, разность арифметической прогрессии, и количество членов арифметической прогрессии. Вывести сумму арифметической прогрессии.', 12, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Номер квартиры', 'В подъезде 12 квартир, в доме 4 подъезда. Задан номер квартиры, найти номер дома и подъезда.', 13, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сумма цифр', 'Задано шестизначное число, найти сумму его цифр.', 14, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Две суммы цифр ', 'Задано восьмизначное число. Проверить равна ли сумма первых его 4 цифр, сумме его последних 4 цифр. Результат вывести на экран в виде TRUE или FALSE.', 15, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Между другими', 'Заданы 3 числа: A, B и C. Проверить находится ли значение числа A, между числами B и С. Результат вывести на экран в виде TRUE или FALSE.', 16, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Куб или квадрат', 'Задано число n. Если число n четное, то вывести n^2, если оно нечетное, то вывести n^3. Не использовать оператор if и case.', 17, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Степень двойки', 'Задано число n. Вывести 2^n. Не использовать операторы цикла.', 18, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Без третьей переменной', 'Поменять значение переменных без использования третьей переменной.', 19, 0);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальный из двух', 'Заданы 2 числа. Вывести на экран максимальный элемент.', 1, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Медиана', 'Заданы 3 числа. Вывести на экран то число, значение которого находится между двумя остальными.', 2, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Логическое выражение', 'Заданы числа x, y, z, r. Посчитать значение логического выражения: (x > y) ^ (x > z) ^ ((r < y) v (r < z)) v !(x >= r) ^ (x*r + y*z > 0) , на экран вывести Выполняется или Не выполняется.', 3, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Корни квадртаного уравнения', 'Заданы коэффициенты a, b, c квадратного уравнения. Вывести на экран все различные корни.', 4, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Полный квадрат', 'Задано число N. Проверить является ли оно полным квадратом (то есть является ли его корень целым числом).', 5, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Шахматный конь', 'Заданы координаты шахматного коня находящегося на шахматной доске. Вывести на экран количество клеток, которое находится у него под ударом.', 6, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальный и минимальный', 'Заданы 4 числа, выбрать среди них максимальный и минимальный элементы и вывести их на экран.', 7, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Точка на плоскости', 'Заданы координаты (x, y) точки M. Определить месторасположение этой точки в системе координат: является ли она началом координат, находится ли на горизонтальной или вертикальной оси, или находится в одной из четверти (вывести на экран в какой).', 8, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Точка и круг', 'Задан радиус круга с центром в точке (0, 0) и точка с координатами (x, y). Определить принадлежит ли точка кругу.', 9, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Ферзь и король', 'Заданы координаты ферзя и короля. Проверить находится ли король под ударом у ферзя.', 10, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Остаток от деления', 'Задано число N. Словами вывести его остаток от деления на 5. Например, если задано число 13, то на экран должно быть выведено слово: Три.', 11, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Учебные задания', 'Дано целое число в диапазоне 10–40, определяющее количество учебных заданий по некоторой теме. Вывести строку-описание указанного количества заданий, обеспечив правильное согласование числа со словами «учебное задание», например: 18 — восемнадцать учебных заданий, 23 — двадцать три учебных задания, 31 — тридцать одно учебное задание. Если пользователь введёт данные не соответствующие условию задачи - выдать сообщение об ошибке.', 12, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Число словами', 'Дано целое число в диапазоне 100–999. Вывести строку-описание данного числа, например: 256 — двести пятьдесят шесть, 814 — восемьсот четырнадцать. Если пользователь введёт данные не соответствующие условию задачи - выдать сообщение об ошибке.', 13, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Восточный календарь', 'В восточном календаре принят 60-летний цикл, состоящий из 12-летних подциклов, обозначаемых названиями цвета: зеленый, красный, желтый, белый и черный. В каждом подцикле годы носят названия животных: крысы, коровы, тигра, зайца, дракона, змеи, лошади, овцы, обезьяны, курицы, собаки и свиньи. По номеру года определить его название, если 1984 год — начало цикла: год зеленой крысы.', 14, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Дата словами', 'Задана дата в виде «день месяц». Вывести словами дату. Например, если задано «12 12», то нужно вывести: Двенадцатое декабря.', 15, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Степень числа', 'Используя оператор цикла for вычислить a^n, при заданных a и n.', 16, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Факториал', 'Используя оператор цикла for вычислить n!, при заданном n.', 17, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Разница в последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить разность между максимальным и минимальным элементами.', 18, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальные в последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить количество максимальных элементов.', 19, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Нулевые в последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить имеются ли в последовательности нулевые элементы.', 20, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Положительные в последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить каких элементов в последовательности больше – положительных или отрицательных.', 21, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Разница количества в последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить разность между количеством максимальных и минимальных элементов.', 22, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Упорядоченность последовательности', 'Дано 10 вещественных чисел. Не используя массивов, определить упорядочена ли последовательность по неубыванию (следующий элемент не меньше предыдущего).', 23, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('День недели', 'Дни недели пронумерованы следующим образом: 1 — понедельник, 2 — вторник, …, 6 — суббота, 7 — воскресенье. Дано целое число K, лежащее в диапазоне 1–365, и целое число N, лежащее в диапазоне 1–7. Определить номер дня недели для K-го дня года, если известно, что в этом году 1 января было днем недели с номером N.', 24, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Таблица умножения', 'Вывести таблицу умножения 9x9.', 25, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Арифметическая прогрессия', 'Заданы первый член арифметической прогрессии, последний член арифметической прогрессии, разность арифметической прогрессии. Вывести арифметическую прогрессию.', 26, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сумма цифр', 'Задано целое число, определить сумму его цифр.', 27, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Три наибольших в последовательности', 'Задана непустая последовательность ненулевых целых чисел, за которой следует 0. Определить три наибольших числа среди них.', 28, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Перемена знака в последовательности', 'Задана непустая последовательность ненулевых целых чисел, за которой следует 0. Определить, сколько раз в этой последовательности меняется знак. (Например, в последовательности 1, -34, 8, 14, -5 знак меняется 3 раза).', 29, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Номер в последовательности', 'Задана непустая последовательность ненулевых целых чисел, за которой следует 0. Найти порядковый номер того из них, которое наиболее близко к указанному целому числу.', 30, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Куб или квадрат в последовательности', 'Дано 10 целых чисел. Для положительных вывести их квадрат, для отрицательных - их куб. Нули не обрабатывать.', 31, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Бесконечная сумма', 'Вычислить бесконечную сумму с заданной точностью eps (eps>0). Считать, что точность достигнута, если очередное слагаемое по модулю меньше eps, - все последующие слагаемые можно уже не учитывать: S=1/1^2 + 1/2^2 + ... + 1/i^2 + ....', 32, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Слова в последовательности', 'Дано 10 целых чисел. Для положительных вывести остаток от деления на 5 словами, для отрицательных ничего не выводить. Использовать оператор continue.', 33, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Корень в последовательности', 'Задана непустая последовательность ненулевых чисел за которыми следует 0. Для четных положительных чисел вывести квадрат, для четных отрицательных - куб, для нечетных положительных - корень, нечетные отрицательные не обрабатывать.', 34, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Таблица 4 на 4', 'Задана таблица 4х4. Найти номер строки и столбца в которых расположен минимальный элемент.', 35, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальный треугольник', 'Известны длины отрезков a, b, c, d. Определить из каких отрезков получится треугольник максимальной и минимальной площади.', 36, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Цифровой корень', 'Цифровой корень числа определяется следующим образом: складываются все цифры заданного числа. Если сумма меньше 10, то она и есть его цифровой корень, иначе также определяется цифровой корень полученной суммы. Задано число N. Определить его цифровой корень.', 37, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Расстояние от точки до отрезка', 'Дан отрезок с координатами x1, y1, x2, y2 и точка с координатами x, y. Определить минимальное расстояние от точки до отрезка.', 38, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Целочисленные решения', 'Найти все целочисленные решения уравнения 2x + 3y = 27, в пределах x ε [-100, 100], y ε [-200, 150].', 39, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сочетания', 'Заданы числа n, k. Посчитать число сочетаний (С) из n по k.', 40, 1);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Случайный массив', 'Задано число n. Заполнить массив состоящий из n элементов случайными числами и вывести его на экран.', 1, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальный и минимальный элемент в массиве', 'Задано число n. Считайте из консоли массив из n элементов. Найти в массиве максимальный элемент и минимальный элемент и вывести их на экран, кроме того вывести на экран их индексы.', 2, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Произведение положительных', 'Задано число n. Считайте из консоли массив из n элементов. На экран вывести произведение положительных элементов массива.', 3, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Четные - нечетные', 'Задано четное число n. Считайте из консоли массив из n элементов. В массиве поменять нечетные элементы с четными (первый со вторым, третий с четвертым и т.д.). Результат вывести на экран.', 4, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сумма элементов', 'Задано четное число n. Считайте из консоли массив из n элементов. Определить что больше: сумма элементов с нечетными индексами или сумма элементов с четными индексами.', 5, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Удаление из массива', 'Задано число n и число m. Считайте из консоли массив из n элементов. В массиве удалить элемент с индексом m и вывести результат на экран. Если в массиве не существует элемента с индексом m (при m < 0 или m >= n), то ничего не делать.', 6, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимин', 'Задано число n. Считайте из консоли массив из n элементов. Определить максимальный и минимальный элементы среди положительных элементов с нечетным индексом. Удалить из массива все нулевые элементы.', 7, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сортировка', 'Задано число n. Считайте из консоли массив из n элементов. Упорядочить его по возрастанию (то есть разместить его элементы так, чтобы каждое следующее число было больше чем предыдущее) и вывести на экран.', 8, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимин 2', 'Задано число n. Считайте из консоли массив из n элементов. Найти индексы минимального и максимального элемента, а также сумму элементов между ними.', 9, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('В другой массив', 'Задано число n. Считайте из консоли массив из n элементов. Записать положительные элементы массива A в массив B, определить m - количество положительных элементов.', 10, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Двумерный массив', 'Заданы числа n и m. Считайте из консоли двумерный массив, состоящий из N строк и M столбцов.', 11, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Замена строки', 'Заданы числа n и m. Считайте из консоли двумерный массив, состоящий из N строк и M столбцов. Заменить строку массива с максимальной суммой элементов на первую строку поэлементно.', 12, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимин 3', 'Заданы числа n и m. Считайте из консоли двумерный массив, состоящий из N строк и M столбцов. Вывести на экран максимальный и минимальный элемент, кроме того вывести номер строки и номер столбца в котором находится максимальный элемент и тоже самое сделать с минимальным элементом.', 13, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимин для строки', 'Заданы числа n и m. Считайте из консоли двумерный массив, состоящий из N строк и M столбцов. Для каждой строки вывести максимальный и минимальный элемент в ней.', 14, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Знаки препинания', 'Задана строка S. Посчитать в ней количество пробелов, точек и запятых. Вывести их количества на экран.', 15, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Русские на английские', 'Задана строка S. Заменить все русские буквы на английские (например дана строка «огород», надо вывести «ogorod»), результат вывести на экран.', 16, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Синусы', 'Задано число N. Вывести строку: sin(1 + sin(2 + sin(...))), в которой количество sin равно числу N. (Например, задано число 4, должно быть выведено: sin(1 + sin(2 + sin(3 + sin(4)))) ).', 17, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Гласные и согласные', 'Задана строка S. Посчитать в ней количество гласных и согласных букв. Результат вывести на экран.', 18, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Лишние пробелы', 'Задана строка S. Удалить в ней два и более подряд идущих пробела. Результат вывести на экран.', 19, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Палиндром', 'Задана строка S. Проверить является ли она палиндромом. Вывести на экран «Палиндром» или «Не палиндром». Палиндромом является строка которая слева направо, читается так как и справа налево, например: казак, арозаупаланалапуазора и т.д..', 20, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Слова', 'Задана строка S. Вывести каждое слово из строки в отдельности (каждое в новой строке). Слова в строке разделяются с помощью пробелов, запятых, точек, знаков вопроса и восклицательных знаков.', 21, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Правильные скобки', 'Задана строка S, которая представляет собой арифметическое выражение. Проверить правильность расстановки скобок в нем. На экран вывести TRUE или FALSE.', 22, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Числа из строки', 'Задана строка S. На экран вывести те слова, которые представляют собой числа.', 23, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Нечетные символы', 'Задана строка S. На экран вывести строку, которая содержит только те символы строки S, которые находятся на нечетных позициях.', 24, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Гласные буквы', 'Задана строка S. Удалить из неё все гласные английские буквы и вывести результат на экран.', 25, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Фибоначчи', 'Задано число n. Посчитать значение n-го числа Фибоначчи. Напомним, что число Фибоначчи вычисляется с помощью рекуррентной формулы: f0 = 1, f1 = 1, fn = fn - 1 + fn - 2, поэтому f2 = 2, f3 = 3, f4 = 5, f5 = 8, ....', 26, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Общая подстрока', 'Заданы две строки. Найти самую большую общую подстроку и вывести её на экран.', 27, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Произведение матриц', 'Заданы две матрицы: A размерностью NxM и B размерностью MxK. На экран вывести матрицу С размерностью NxK, которая будет являться произведением матриц A и B.', 28, 2);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Максимальные', 'Определить функции int mx(int x, int y), которая возвращает максимальное из чисел x и y, и mn(int x, int y), которая возвращает минимальное из чисел x и y. Заданы числа a, b, c и d. Используя данные функции определить максимум и минимум из этих чисел.', 1, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Степень', 'Написать функцию int powr(int x, int y), которая возвращает x^y, без использования функции Math.Power(...).', 2, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Двоичные', 'Написать процедуру bin(int x), которая будет печатать двоичное представление числа x.', 3, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Десятичные', 'Написать функцию int dec(String s), которая переводит двоичное представление числа s (заданного в виде строки) в десятичное представление.', 4, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Факториал', 'Написать функцию int factorial(int x), которая будет вычислять факториал числа x. Задаётся число N. Посчитать значение выражения 1! + 2! + 3! + ... + N!.', 5, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Брейк', 'Написать процедуру bryak(int x), которая разбивает число x с четным количеством разрядов на двузначные числа и суммирует их. Например задано число 412513, программа должна выводить: 79 (так как 41+25+13 = 79).', 6, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Соседнее', 'Написать функцию int neigh(int x, int n), которая находит такое число y (кратное n), которое наиболее близко к числу x.', 7, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Вектор', 'Описать класс vector, который хранит поля: int a[128] - которое хранит массив чисел, n - которое хранит размерность массива. Определить методы: fill() - который заполняет вектор случайными числами от -9 до 9; printout() - который выводит вектор на экран.', 8, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Матрица', 'Описать класс matrix, который хранит поля: int a[128][128] - которое хранит двумерную матрицу; n, m - которые хранят размерность матрицы. Определить методы: fill() - который заполняет матрицу случайными числами от -9 до 9; printout() - который выводит матрицу на экран.', 9, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Симметричная', 'Написать функцию int check(Matrix m), которая проверяет является ли матрица m симметричной (т.е. m.a[i, j] == m.a[j, i]).', 10, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Поворот матрицы', 'Написать процедуру rotate(Matrix m), которая поворачивает матрицу на 90 градусов. Матрица и её размерность NxM являются глобальными переменными.', 11, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Простое число', 'Написать функцию, которая для заданного числа определяет является ли оно простым. Задается число N и затем N целых чисел, для каждого из них определить является ли оно простым.', 12, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Счастливые билеты', 'Задаются числа A и B. Написать программу, которая выводит все счастливые билеты между числами A и B.', 13, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Наибольший корень', 'Написать функцию, которая принимает в качестве аргументов числа a, b и c и возвращать значение наибольшего из корней уравнения ax^2+bx+c = 0.', 14, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Удаление цифры', 'Написать функцию, которая принимает в качестве аргументов одно шестизначное число x и одно число n (n находится в пределах от 1 до 6). Заданная функция должна будет удалять n-ую цифру в числе x. Например если в функцию передается число 432758 и число 2, то должно быть возвращено число 43278.', 15, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сумма столбца', 'Задается двумерный массив A, размерностью NxM (N строк и M столбцов). Написать функцию, которая будет принимать в качестве параметра одно число x (1 ≤ x ≤ M) и суммировать все числа в x-ом столбце.', 16, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Поворот вектора', 'Описать класс, который будет хранить информацию о векторах в двумерном пространстве. Описать метод который будет поворачивать вектор на α градусов по часовой стрелке. Описать метод, который будет выводить координаты вектора.', 17, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Человек', 'Описать класс, который будет хранить информацию о человеке: Фамилия, Имя, Отчество (строковый тип данных) и дату рождения (типа данных DateTime). Описать метод, который будет определять его текущий возраст. Описать метод, который будет выводить Фамилию, Имя, Отчество и возраст данного человека.', 18, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сумма между индексами', 'Задается число N и M. Одномерный массив из N элементов заполняется случайным образом. Далее вводятся M пар чисел l и r, для каждой такой пары вывести сумму чисел в массиве между l и r элементами включительно.', 19, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Числа в строке', 'Считать из консоли текст, в этом тексте найти все числа и вывести их на экран. Например, если файл содержит текст: «21 ноября 2012 года, я взял и потерял 28 рублей.», то должны быть выведены числа 21, 2012, 28.', 20, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Слова перевертыши', 'Считать из консоли текст, в этом тексте перевернуть каждое слово и получившийся текст вывести на экран. Слова отделяются друг от друга пробелами, запятыми, точками, восклицательными и вопросительными знаками.', 21, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Сортировка', 'В консоли записаны целые числа. Считать их из файла, упорядочить по возрастанию и записать в консоль.', 22, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Произведение матриц', 'Написать функцию, которая будет вычислять произведению двух матриц, или говорить о том что это сделать невозможно. Описать класс для хранения матрицы Матрицы будут передаваться в качестве параметров функции. Функция также должна возвращать матрицу.', 23, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Факториал', 'Задано число N. Посчитать факториал числа N используя рекурсию.', 24, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Фибоначчи', 'Задано число N. Посчитать значение N-го числа Фибоначчи используя рекурсию.', 25, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Перестановки', 'Задано число N. Напечатать все перестановки чисел 1..N.', 26, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Длинная арифметика', 'Описать класс, который будет реализовывать сложение больших чисел (максимальное число разрядов в таком числе 100, т.е. максимум 100 цифр).', 27, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Обратная матрица', 'Написать функцию, которая для заданной квадратной матрицы, находит обратную ей матрицу.', 28, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Определитель матрицы', 'Написать функцию, которая будет вычислять определитель матрицы. Описать класс, который будет хранить матрицу. Матрица будет передаваться в функцию в качестве параметра. Функция должна возвращать число типа double.', 29, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Острова', 'Задано число N. Считать из консоли двумерный массив NxN, заполненный нулями и единицами. Посчитать количество «островов» в нем. Островом считается блок из нулей и единиц имеющих общую сторону.', 30, 3);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 1', 'Задано число N. Затем задаются N пар, которые представляют собой фамилию гонщика и время за которое он проехал трассу. Отсортировать список по времени. Если время совпадает, то сортировать по фамилии.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 2', 'В первых двух строках заданы два числа n и m, такие, что 0 ≤ n ≤ 10^100, 0 ≤ m ≤ 10^100. Во второй строке задается знак операции («+» / «-»). В зависимости от знака вывести результат выполнения операции.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 3', 'В первых двух строках заданы два числа n и m, такие, что 0 ≤ n ≤ 10^100, 0 ≤ m ≤ 10^100. Вывести результат произведения заданных чисел.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 4', 'В первых четырех строках заданы 4 числа n, a, b. Проверить записано ли число n в a-ичной системе счисления. Если число n является a-ичным, то перевести его в b-ичную систему счисления.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 5', 'Игра «Быки и Коровы». Программа случайно выбирает четырехзначное число. Угадать это число. На каждом шаге играющий называет 4-хзначное число, программа сообщает об угаданных цифрах (быки) и стоящих на месте (коровы). Если число угадывается, то выдается сообщение о том что число угадано и количество сделанных шагов.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 6', 'Игра «Крестики-Нолики». Человек играет с компьютером на поле 3x3. На каждом шаге человек задает координаты куда он ставит крестик. В зависимости от координат компьютер выдает сообщение о том, что данное поле уже занято, о том что игрок выиграл или проиграл. После сделанного хода выдается поле с отмеченными полями занятыми крестиками, ноликами и пустыми полями.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 7', 'Игра «Баше». Допустим на столе лежит N спичек. За ход игрок может взять от 1 до K спичек. Проигрывает тот кто берет спичку последней. Для заданных чисел N и K, реализовать игру человека и компьютера для данных чисел.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 8', 'В первой строке задано число N. Затем вводится матрица размером NxN. Посчитать значение определителя данной матрицы.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 9', 'В первой строке задано число N. Затем вводится матрица размером NxN + 1, которая представляет собой систему линейных уравнений. Необходимо разработать программу, которая будет решать данную систему уравнений методом Гаусса.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 10', 'В первой строке задано число N. Затем задаются две матрицы размером NxN. На экран вывести результат поэлементного сложения матриц, поэлементного вычитания матриц, и результат произведения матриц.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 11', 'Заданы четыре числа: a, b, c, d и один из знаков «+», «/», «-», «*». В соответствии со знаком вывести результат операции над дробями a/b и c/d. Сократить числитель и знаменатель на наибольший общий делитель.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 12', 'Задана дата в виде день, месяц, год. Не используя средства языка программирования вычислить день недели на которые выпадает данная дата. Вывести календарь на заданный месяц.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 13', 'Заданы 5 чисел, которые представляют собой количество набранных голосов кандидатами в президенты. Вывести диаграмму того, как распределились голоса по процентам.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 14', 'Игра «Угадай число». Программа с помощью датчика случайных чисел выбирает число в диапазоне от 0 до 1000. Угадать это число за 10 попыток. После каждой попытки сообщает, больше или меньше названное задуманного.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 15', 'Игра «Покер». Игроку выдается 5 карт. Затем игрок вводит количество карт от 0 до 5 которые он хочет поменять, затем игрок вводит номера карт, которые он хочет поменять. После этого компьютер меняет карты и выводит на экран текущие карты игрока и ту конфигурацию, которую они составляют: пара, тройка, две пары, пара и тройка, стрит, фул хаус, покер.', 1, 4);
INSERT INTO Task(Name, Description, LabTaskNum, LabNum) VALUES ('Курсовая работа 16', 'В первых двух строках заданы два многочлена. Например, запись 2x+2x2+3x+4 определяет многочлен 2x + 2x^2 + 3x + 4. Необходимо вывести произведение многочленов. Например, для входных данных: 2x+2x2 и 3x+4 необходимо вывести: 6x3+14x2+8x', 1, 4);